class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = { lat: null };
        window.navigator.geolocation.getCurrentPosition(
            (position) => console.log(position),
            (err) => console.log(err)
        );
    }
    render() {
        return <div >
            Latitude: { this.state.lat } <
            /div>
    }
}
ReactDOM.render( < App / > , document.getElementById("root"));
